shiny integration
library(shiny)

ui &lt;- fluidPage(handsontableOutput("table"))

server &lt;- function(input, output) {
  output$table &lt;- renderHandsontable({
    handsontable(iris[1:10, ]) |&gt;
      hot_context_menu()
  })

  # detect changes:
  observeEvent(input$table, {
    updated_data &lt;- hot_to_r(data = input$table)
    cat("Updated data:\n")
    print(updated_data)
    # use updated data...
  })
}

shinyApp(ui, server)
